package module1;

public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int result = +1;
		System.out.println(result);
		
		result--;
		//results is 0
		System.out.println(result);
		
		result++;
		//result is 1 now
		System.out.println(result);
		
		result = -result;
		//result is -1 now
		System.out.println(result);
		
		boolean success = false;
		System.out.println(success);
		System.out.println(!success);

	}

}
